const Categories = ({ items }) => {

  const [activeItem,setActiveItem] = React.useState(null)
  return ( 
<div className="categories">
              <ul>
                <li >Все</li>
        {
          items.map((name,index) => <li className={activeItem === index ? 'active' : ''} onClick={()=> onSelectItem} key={`${name}_${index}`}>{ name}</li>)
                }
            </ul>
            </div>
   );
}

export default Categories;
